package test_package;

public class Factorial_Of_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		  int num=4,i,fact=1;
		  
		  for(i=1;i<=num;i++)
		  {    
		      fact*=i;    
		  }    
		  System.out.println("Factorial of "+num+" is : "+fact); 

	}

}
